    private void buttonConvertActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buttonConvertActionPerformed
        double from = Double.parseDouble(textCurrency1.getText());
        textCurrency2.setText(String.format("%.2f",exchangeMoney(from, labelCurrency1.getText())));
    }//GEN-LAST:event_buttonConvertActionPerformed

    private void buttonChooseActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buttonChooseActionPerformed
        String temp = labelCurrency2.getText();
        labelCurrency2.setText(labelCurrency1.getText());
        labelCurrency1.setText(temp);
        String t = textCurrency2.getText();
        textCurrency2.setText(textCurrency1.getText());
        textCurrency1.setText(t);
    }//GEN-LAST:event_buttonChooseActionPerformed
    
    private double exchangeMoney (double money, String currencyFromTo){
        return money* Currencies.get(currencyFromTo); 
    }  
    
    
